    Clients[] clients;

    int idClient;
    string name;
    string telephone;
    string contactPerson;
    string address;

    private enum MODE { None, Add, EditDelete };
    MODE mode = MODE.None;
    Clients client;


    protected override async Task OnInitializedAsync()
    {

        await load();
    }
    protected async Task load()
    {
        clients = await clientsBlazorService.GetClientsAsync();
    }

    protected async Task Insert()
    {
        Clients c = new Clients()
        {

            Name = name,
            Telephone = telephone,
            ContactPerson = contactPerson,
            Address = address
        };
        await clientsBlazorService.InsertClientAsync(c);
        ClearFields();
        await load();
        mode = MODE.None;
    }
    protected void ClearFields()
    {
        name = string.Empty;
        telephone = string.Empty;
        contactPerson = string.Empty;
        address = string.Empty;
    }
    protected void Add()
    {
        ClearFields();
        mode = MODE.Add;
    }
    protected async Task Update()
    {

        Clients c = new Clients()
        {
            IdClient = idClient,
            Name = name,
            Telephone = telephone,
            ContactPerson = contactPerson,
            Address = address
        };

        await clientsBlazorService.UpdateClientAsync(idClient.ToString(), c);
        ClearFields();
        await load();
        mode = MODE.None;
    }

    protected async Task Delete()
    {
        await clientsBlazorService.DeleteClientAsync(idClient.ToString());
        ClearFields();
        await load();
        mode = MODE.None;
    }
    protected async Task Show(string id)
    {
        client = await clientsBlazorService.GetClientByIdAsync(id);
        idClient = client.IdClient;
        name = client.Name;
        telephone = client.Telephone;
        contactPerson = client.ContactPerson;
        address = client.Address;
        mode = MODE.EditDelete;
    }

